import React from 'react';
import './Card.css';

const Card = props => {
  return (
      <div className={props.show? "show": "Card"} style={{display: props.hide ? 'none' : 'block' }} onClick={props.handleClick}>
        <img src={props.src} alt={props.title} title={props.title}
             id="logo" width={props.imgWidth} height={props.imgHeight}
        />


          <span id="title">{props.title}</span>
        <span id="description">{props.description}</span>
      </div>
  )
}

export default Card;
