import React, { Component } from 'react';
import './App.css';
import './mouse.css';
import { TimelineLite, TweenLite, CSSPlugin } from "gsap/all";
import { FiGithub, FiLinkedin, FiFileText } from "react-icons/fi";
import resume from './assets/resume.pdf';

import Scene from './components/Scene';
import Card from './components/Card';

class App extends Component {
  constructor() {
    super();
    this.state = {
      page: 1,
      pageTo: null,
      cards: [
        {
          src: require("./assets/Empire.png"),
          srcWidth: 100,
          srcHeight: 100,
          title: "Empire Life, Innovation Lab",
          description: "Software Developer",
          body: `Core member of the innovation team, producing research and development regarding emerging technologies
Developed a chatbot module using React/Redux, Node/Express and Firebase handling user queries using Google Dialogflow as a NLP 
Worked under a fast paced development cycle
(Frontend) Performed the role of a lead frontend developer, designing the architecture`
        },
        {
          src: require("./assets/q4.svg"),
          srcWidth: 100,
          srcHeight: 100,
          title: "Q4 Inc.",
          description: "QA Automation Developer",
          body: ""
        },
        {
          src: require("./assets/mockup1.png"),
          srcWidth: 170,
          srcHeight: 100,
          title: "Message",
          description: "Messaging/Video Chat app",
          body: ""
        },
        {
          src: require("./assets/home.png"),
          srcWidth: 100,
          srcHeight: 100,
          title: "RentForYou",
          description: "Classified Ads for sublets",
          body: ""
        }
      ],
      showCard: null,
    };

    this.cardsTween = null;

    this.headerMain = null;
    this.headerSecondary = null;
    this.myTween = null;
    this.cards = [];
    this.page2 = null
  }

  authenticate(){
    return new Promise(resolve => setTimeout(resolve, 2000))
  }

  componentDidMount() {

    this.authenticate().then(() => {
      const ele = document.getElementById('loading-screen')
      if(ele){
        // fade out
        ele.classList.add('available')
        this.headerTween.restart();

        setTimeout(() => {
          // remove from DOM
          ele.outerHTML = '';
        }, 2000)
      }
    })

    window.addEventListener('wheel', this.handleScroll, false);
    this.headerTween = new TimelineLite({paused: false})
      .to( this.cards, 0, {autoAlpha: 0})
      .from(this.headerMain, 1, {autoAlpha:0, y: 100})
      .from(this.headerSecondary, 1, {autoAlpha:0, y: 100})

    this.bodyTweenIn = new TimelineLite({ paused: true })
      .to(this.headerSecondary, 0.5, {autoAlpha:0, y: 100})
      .to(this.headerMain, 0.5, {autoAlpha:0, y: 100})
      .staggerTo( this.cards , 0.5, { autoAlpha: 1, y: 20 }, 0.1)
      .add(() => {this.setState({pageTo: null})} )

    this.bodyTweenOut = new TimelineLite({ paused: true })
      .staggerTo( this.cards , 0.5, { autoAlpha: 0, y: -20 }, 0.1)
      .from(this.headerMain, 0.5, {autoAlpha:0, y: -10})
      .from(this.headerSecondary, 0.5, {autoAlpha:0, y: -10})
      .add(() => {this.setState({pageTo: null})} )
  }

  handleScroll = (e) => {
    e.preventDefault();
    if(e.deltaY > 0 && this.state.pageTo === null && this.state.page !== 2) {
      this.setState({
        pageTo: 2,
        page: 2
      })
    } else if(e.deltaY < 0 && this.state.pageTo === null && this.state.page !== 1){
      this.setState({
        pageTo: 1,
        page: 1
      })
    }
  }

  openCard = (index) => {
    console.log(index)
    if (this.state.showCard === null) {
      this.setState({
        showCard: index
      })
    }
  }

  closeCard = (index) => {
    console.log('test')
      this.setState({
        showCard: null
      })
  }


  render() {
    if (this.state.pageTo === 1) {
      this.bodyTweenOut.restart();
      // this.headerTween.restart();
    } else if(this.state.pageTo === 2) {
      this.bodyTweenIn.restart();
    }

    return (
      <div id="master-wrapper" >
        <Scene />
        <div id="header-main" ref={(i) => {this.headerMain = i}}>
          <span>HI, I'M </span>
          <span id="name">BRIAN</span>
        </div>
        <div id="header-secondary" ref={(i) => {this.headerSecondary = i}}>
          SOFTWARE DEVELOPER
        </div>
        {this.state.page===1 && <div className="mouse"><span></span></div>}
        <div id="icon-container">
          <a href={resume} target="_blank" rel="noopener noreferrer">
            <div className="icon">
              <FiFileText />
            </div>
          </a>
          <a 
            href="https://github.com/brnjlee" 
            rel="noopener noreferrer" 
            target="_blank"
          >
            <div className="icon">
              <FiGithub />
            </div>
          </a>
          <a 
            href="https://www.linkedin.com/in/brian-lee-2553a1149/" 
            rel="noopener noreferrer" 
            target="_blank"
          >
            <div className="icon">
              <FiLinkedin />
            </div>
          </a>
        </div>
        <div className="page-indicator">
          <span className={this.state.page===1 ? "active" : null}/>
          <span className={this.state.page===2 ? "active" : null}/>
        </div>
        <div id="body">
            <div ref={(ref) => {this.cards.push(ref)}}>
              <span id="section-title">EXPERIENCE / PROJECTS</span>
            </div>

            <div id="section-body">
            {
            this.state.cards.map((element, index) => (
              <div key={index} ref={(ref) => {this.cards.push(ref)}} className={this.state.showCard === index && "show-card"}>
                <Card 
                  src={element.src}
                  imgWidth={element.srcWidth} 
                  imgHeight={element.srcHeight} 
                  title={element.title} 
                  description={element.description}
                  openCard={() => this.openCard(index)}
                  closeCard={() => this.closeCard(index)}
                  show={this.state.showCard === index}
                  hide={this.state.showCard !== index && this.state.showCard !== null}
                />
              </div>
            ))}
            </div>
        </div>
      </div>
    );
  }
}

export default App;
