import React from 'react';
import './Card.css';
import { FiXCircle } from "react-icons/fi";

const Card = props => {
  createMarkup =() => {
    return {__html: props.body};
  }

  return (
      <div 
        className={props.show? "show Card": "hide Card"} 
        style={{display: props.hide ? 'none' : null }} 
        onClick={props.openCard}
      >
        {props.show && <FiXCircle id="close-button" onClick={props.closeCard}/>}
        <img src={props.src} alt={props.title} title={props.title}
             id="logo" width={props.imgWidth} height={props.imgHeight}
        />
        <span id="title">{props.title}</span>
        <span id="description">{props.description}</span>
        <hr />
        {props.show && <div dangerouslySetInnerHTML={createMarkup()} ></div>}
      </div>
  )
}

export default Card;
